$(document).ready(function(){$(".submit-a-quote").on("submit",function(e){e.preventDefault();const t=$(".submit-author").val(),o=$(".submit-quote").val(),r=$(".submit-source").val(),u=$(".submit-url").val();$.ajax({method:"POST",url:red_vars.rest_url+"wp/v2/posts",data:{title:t,content:o,_qod_quote_source:r,link:u,comment_status:"closed"},beforeSend:function(e){e.setRequestHeader("X-WP-Nonce",red_vars.wpapi_nonce)}}).done(function(e){console.log("Submitting Post!"),alert("Thankyou for Submitting!.")})}),$(".get-quote-button").on("click",function(){$(".quote");const e=$(".author");$.ajax({method:"GET",url:red_vars.rest_url+"/wp/v2/posts?filter[orderby]=rand&filter[posts_per_page]=1"}).done(t=>{console.log(t),e.html("— "+t[0].title.rendered),$(".quote > p").css("margin-top","0rem"),t[0]._qod_quote_source_url?e.html("— "+t[0].title.rendered+",&nbsp<a href="+t[0]._qod_quote_source_url+">"+t[0]._qod_quote_source+"</a>"):t[0]._qod_quote_source?e.html("— "+t[0].title.rendered+", "+t[0]._qod_quote_source):e.html("— "+t[0].title.rendered)})})});