$(document).ready(function(){console.log("ready!");const o=$(".quote"),t=$(".author");$(".get-quote-button").on("click",function(){$.ajax({url:"api/getQuotes",method:"GET"}).done(e=>{o.html(e.quote),t.html(e.author),console.log(e)}).fail(e=>{o.html("New Quote"),t.html("New Author"),alert("Quote Is Not Found"),console.error("Failed To Get Quotes",e)})})});