$(document).ready(function(){$(".submit-a-quote").on("submit",function(e){e.preventDefault();const t=$(".submit-author").val(),o=$(".submit-quote").val(),r=$(".submit-source").val(),u=$(".submit-url").val();$.ajax({method:"POST",url:red_vars.rest_url+"wp/v2/posts",data:{title:t,content:o,_qod_quote_source:r,_qod_quote_source_url:u,comment_status:"closed"},beforeSend:function(e){e.setRequestHeader("X-WP-Nonce",red_vars.wpapi_nonce)}}).done(function(e){console.log("Submitting Post!"),alert("Thankyou for Submitting!.")}).fail(()=>{alert("Sorry, there was an error submitting.")})}),$(".get-quote-button").on("click",function(){const e=$(".quote"),t=$(".author");$.ajax({method:"GET",url:red_vars.rest_url+"/wp/v2/posts?filter[orderby]=rand&filter[posts_per_page]=1"}).done(o=>{console.log(o),e.html(o[0].content.rendered),$(".quote > p").css("margin-top","0rem"),o[0]._qod_quote_source_url?t.html("— "+o[0].title.rendered+",&nbsp<a href="+o[0]._qod_quote_source_url+">"+o[0]._qod_quote_source+"</a>"):o[0]._qod_quote_source?t.html("— "+o[0].title.rendered+", "+o[0]._qod_quote_source):t.html("— "+o[0].title.rendered)})})});